                                        {ratings.map(rating =>{
                                            return(
                                                <option value={rating}>{rating}</option>
                                            )
                                        })}
                                </Form.Control>
                            </Form.Group>
                            <Button
                                variant="primary"
                                type="button"
                                onClick={findByRating}
                            >
                                Search
                            </Button>
                        </Col>
                    </Row>
                </Form>
            </Container>
        </div>
